(()=>{const e=document.getElementById("darkmode-toggle"),o=document.querySelector(".navbar"),l=document.querySelector(".side-menu"),t=document.querySelector(".right-panel");e.addEventListener("input",(()=>{e.checked?(o.style.backgroundColor="#202025",o.style.color="white",l.style.backgroundColor="#2b2b2e",l.style.color="white",t.style.backgroundColor="#1a1a1b",t.style.color="white"):(o.style.backgroundColor="#FAF6F5",o.style.color="black",l.style.backgroundColor="#F7F8F3",l.style.color="black",t.style.backgroundColor="#f8f3f0",t.style.color="black")}))})();